def CountOnes(N, L, R):
    if (L > R):
        return 0
    if (N <= 1):
        return N
    ret = 0
    M = N // 2
    Siz_M = findSize(M)
    if (L <= Siz_M):
        ret += CountOnes(N // 2, L, min(Siz_M, R))
    if (L <= Siz_M + 1 and Siz_M + 1 <= R):
        ret += N % 2
    if (Siz_M + 1 < R):
        ret += CountOnes(N // 2, max(1, L - Siz_M - 1), R - Siz_M - 1)
    return ret


