def dfs(i, j, grid, vis, ans, z, z_count):
    n = len(grid)
    m = len(grid[0])
    vis[i][j] = 1
    if (grid[i][j] == 0):
        z += 1
    if (grid[i][j] == 2):
        if (z == z_count):
            ans += 1
        vis[i][j] = 0
        return grid, vis, ans
    if (i >= 1 and not vis[i - 1][j] and grid[i - 1][j] != -1):
        grid, vis, ans = dfs(i - 1, j, grid, vis, ans, z, z_count)
    if (i < n - 1 and not vis[i + 1][j] and grid[i + 1][j] != -1):
        grid, vis, ans = dfs(i + 1, j, grid, vis, ans, z, z_count)
    if (j >= 1 and not vis[i][j - 1] and grid[i][j - 1] != -1):
        grid, vis, ans = dfs(i, j - 1, grid, vis, ans, z, z_count)
    if (j < m - 1 and not vis[i][j + 1] and grid[i][j + 1] != -1):
        grid, vis, ans = dfs(i, j + 1, grid, vis, ans, z, z_count)
    vis[i][j] = 0
    return grid, vis, ans


