def build(sum, a, l, r, rt):
    if (l == r):
        sum[rt] = a[l - 1]
        return
    m = (l + r) >> 1
    build(sum, a, l, m, rt << 1)
    build(sum, a, m + 1, r, rt << 1 1)


def pushDown(sum, add, rt, ln, rn):
    if (add[rt]):
        add[rt << 1] += add[rt]
        add[rt << 1 1] += add[rt]
        sum[rt << 1] += add[rt] * ln
        sum[rt << 1 1] += add[rt] * rn
        add[rt] = 0


def update(sum, add, L, R, C, l, r, rt):
    if (L <= l and r <= R):
        sum[rt] += C * (r - l + 1)
        add[rt] += C
        return
    m = (l + r) >> 1
    pushDown(sum, add, rt, m - l + 1, r - m)
    if (L <= m):
        update(sum, add, L, R, C, l, m, rt << 1)
    if (R > m):
        update(sum, add, L, R, C, m + 1, r, rt << 1 1)


def queryy(sum, add, L, R, l, r, rt):
    if (L <= l and r <= R):
        return sum[rt]
    m = (l + r) >> 1
    pushDown(sum, add, rt, m - l + 1, r - m)
    ans = 0
    if (L <= m):
        ans += queryy(sum, add, L, R, l, m, rt << 1)
    if (R > m):
        ans += queryy(sum, add, L, R, m + 1, r, (rt << 1 1))
    return ans


