import math


def PrimeFactor(N):
    ANS = dict()
    while N % 2 == 0:
        if 2 in ANS:
            ANS[2] += 1
        else:
            ANS[2] = 1
        N = N // 2
    for i in range(3, int(math .sqrt(N)) + 1, 2):
        while N % i == 0:
            if i in ANS:
                ANS[i] += 1
            else:
                ANS[i] = 1
            N = N // i
    if 2 < N:
        ANS[N] = 1
    return ANS


def CountToMakeEqual(X, Y):
    GCD = math .gcd(X, Y)
    newY = X // GCD
    newX = Y // GCD
    primeX = PrimeFactor(newX)
    primeY = PrimeFactor(newY)
    ans = 0
    for factor in primeX:
        if X % factor != 0:
            return -1
        ans += primeX[factor]
    for factor in primeY:
        if Y % factor != 0:
            return -1
        ans += primeY[factor]
    return ans


