def recurSelectionSort(a, n, index=0):
    if index == n:
        return -1
    k = minIndex(a, index, n - 1)
    if k != index:
        a[k], a[index] = a[index], a[k]
        a[k], a[index] = a[index], a[k]
    recurSelectionSort(a, n, index + 1)


