    newHead = head
    latestVowel = None
    curr = head
    if (head is None):
        return None
    if (isVowel(head .data)):
        latestVowel = head
    else:
        while (curr .next is not None and not isVowel(curr .next .data)):
            curr = curr .next
        if (curr .next is None):
