def KthLargestUsingMorrisTraversal(root, k):
    curr = root
    Klargest = None
    count = 0
    while (curr is not None):
        if (curr .right is None):
            count += 1
            if (count == k):
                Klargest = curr
            curr = curr .left
        else:
            succ = curr .right
            while (succ .left is not None and succ .left != curr):
                succ = succ .left
            if (succ .left is None):
                succ .left = curr
                curr = curr .right
            else:
                succ .left = None
                count += 1
                if (count == k):
                    Klargest = curr
                curr = curr .left
    return Klargest


