def findCrossOver(arr, low, high, x):
    if (arr[high] <= x):
        return high
    if (arr[low] > x):
        return low
    mid = (low + high) // 2
    if (arr[mid] <= x and arr[mid + 1] > x):
        return mid
    if (arr[mid] < x):
        return findCrossOver(arr, mid + 1, high, x)
    return findCrossOver(arr, low, mid - 1, x)


