def checkIsAP(arr, n):
    hm = {}
    smallest = float('inf')
    second_smallest = float('inf')
    for i in range(n):
        if (arr[i] < smallest):
            second_smallest = smallest
            smallest = arr[i]
        elif (arr[i] != smallest and arr[i] < second_smallest):
            second_smallest = arr[i]
        if arr[i]not in hm:
            hm[arr[i]] = 1
        else:
            return False
    diff = second_smallest - smallest
    for i in range(n - 1):
        if (second_smallest)not in hm:
