        return head
    prev, curr = head, head .next
    while (curr):
        if (prev .data > curr .data):
            prev .data, curr .data = curr .data, prev .data
        if (curr .next and curr .next .data > curr .data):
            curr .next .data, curr .data = curr .data, curr .next .data
        prev = curr .next
        if (not curr .next):
