def countLessThan(arr, n, key):
    l = 0
    r = n - 1
    index = -1
    while (l <= r):
        m = (l + r) // 2
        if (arr[m] < key):
            l = m + 1
            index = m
        else:
            r = m - 1
    return (index + 1)


def countGreaterThan(arr, n, key):
    l = 0
    r = n - 1
    index = -1
    while (l <= r):
        m = (l + r) // 2
        if (arr[m] <= key):
            l = m + 1
        else:
            r = m - 1
            index = m
    if (index == -1):
        return 0
    return (n - index)


