def findMinimumChanges(N, K, S):
    ans = 0
    for i in range((K + 1) // 2):
        mp = {}
        for j in range(i, N, K):
            mp[S[j]] = mp .get(S[j], 0) + 1
        j = N - i - 1
        while (j >= 0):
            if ((K & 1) and (i == K // 2)):
                break
            mp[S[j]] = mp .get(S[j], 0) + 1
            j -= K
        curr_max = -sys .maxsize - 1
        for key, value in mp .items():
            curr_max = max(curr_max, value)
        if ((K & 1) and (i == K // 2)):
            ans += (N // K - curr_max)
        else:
            ans += (N // K * 2 - curr_max)
    return ans


