    low = 0
    high = len(primes) - 1
    while (low <= high):
        mid = (low + high) // 2
        if (primes[mid] == diff):
            return primes[mid]
        elif (primes[mid] < diff):
            low = mid + 1
        else:
            res = primes[mid]
            high = mid - 1
    return res


