    if (N == 1):
        return M
    if (N > M):
        for i in range(1, M + 1):
            numerator = N * i - N + M - i
            denominator = M - 1
            if (numerator % denominator == 0):
                j = numerator / denominator
                if (j >= 1 and j <= N):
                    count += 1
    else:
        for j in range(1, N + 1):
            numerator = M * j - M + N - j
            denominator = N - 1
            if (numerator % denominator == 0):
                i = numerator / denominator
                if (i >= 1 and i <= M):
                    count += 1
    return count


