    temp = root
    for i in range(idx, len(s)):
        if temp .child[ord(s[i]) - ord('a')] is None:
            temp .child[ord(s[i]) - ord('a')] = TrieNode()
        temp = temp .child[ord(s[i]) - ord('a')]


