def isWasteful(n):
    if (n == 1):
        return False
    original_no = n
    sumDigits = 0
    while (original_no > 0):
        sumDigits += 1
        original_no = original_no // 10
    pDigit, count_exp, p = 0, 0, 0
    i = 0
    while (primes[i] <= (n // 2)):
        while (n % primes[i] == 0):
            p = primes[i]
            n = n // p
            count_exp += 1
        while (p > 0):
            pDigit += 1
            p = p // 10
        while (count_exp > 1):
            pDigit += 1
            count_exp = count_exp // 10
        i += 1
    if (n != 1):
        while (n > 0):
            pDigit += 1
            n = n // 10
    return bool(pDigit > sumDigits)


