                    if (j + 1 <= n - 1):
                        dp[i][j] += dp[i][j + 1]
                    if (i - 1 < 0 or j + 1 >= n):
                        dp[i][j] += 1
                elif (s[i] != s[j]):
                    if (i - 1 >= 0):
                        dp[i][j] += dp[i - 1][j]
                    if (j + 1 <= n - 1):
