def binarySearch(left, right, n):
    if (left <= right):
        mid = int((left + right) / 2)
        if (mid == 0 or mid == len(primes) - 1):
            return primes[mid]
        if (primes[mid] == n):
            return primes[mid - 1]
        if (primes[mid] < n and primes[mid + 1] > n):
            return primes[mid]
        if (n < primes[mid]):
            return binarySearch(left, mid - 1, n)
        else:
            return binarySearch(mid + 1, right, n)
    return 0


