def Betrothed_Sum(n):
    Set = []
    for number_1 in range(1, n):
        sum_divisor_1 = 1
        i = 2
        while i * i <= number_1:
            if (number_1 % i == 0):
                sum_divisor_1 = sum_divisor_1 + i
                if (i * i != number_1):
                    sum_divisor_1 += number_1 // i
            i = i + 1
        if (sum_divisor_1 > number_1):
            number_2 = sum_divisor_1 - 1
            sum_divisor_2 = 1
            j = 2
            while j * j <= number_2:
                if (number_2 % j == 0):
                    sum_divisor_2 += j
                    if (j * j != number_2):
                        sum_divisor_2 += number_2 // j
                j = j + 1
            if (sum_divisor_2 == number_1 +
                    1 and number_1 <= n and number_2 <= n):
                Set .append(number_1)
                Set .append(number_2)
    Summ = 0
