def lexicographicallyMaximum(S, N):
    M = {}
    for i in range(N):
        if S[i] in M:
        M[S[i]] += 1
        else:
            M[S[i]] = 1
    V = []
    for i in range(ord('a'), ord('a') + min(N, 25)):
        if i not in M:
            V .append(chr(i))
    j = len(V) - 1
    for i in range(N):
        if (ord(S[i]) >= (ord('a') + min(N, 25))
                or (S[i] in M and M[S[i]] > 1)):
            if (ord(V[j]) < ord(S[i])):
                continue
            M[S[i]] -= 1
            S = S[0:i] + V[j] + S[(i + 1):]
            j -= 1
        if (j < 0):
            break
    l = 0
    for i in range(N - 1, -1, -1):
        if (l > j):
            break
        if (ord(S[i]) >= (ord('a') + min(N, 25)) or S[i] in M and M[S[i]] > 1):
            M[S[i]] -= 1
            S = S[0:i] + V[l] + S[(i + 1):]
            l += 1
    s = list(S)
    s[len(s) - 1] = 'd'
    S = "".join(s)
