        for i in range(3, n + 1, 2):
            if i * i > n:
                break
            if SPF[i] == i:
                for j in range(i * i, n + 1, i):
                    if SPF[j] == j:
                        SPF[j] = i
        prime_pow = {}
        for i in range(r + 1, n + 1):
            t = i
            while t > 1:
                if not SPF[t] in prime_pow:
                    prime_pow[SPF[t]] = 1
                else:
                    prime_pow[SPF[t]] += 1
                t //= SPF[t]
        for i in range(1, n - r + 1):
            t = i
            while t > 1:
                prime_pow[SPF[t]] -= 1
                t //= SPF[t]
        ans = 1
        mod = 10 ** 9 + 7
        for i in prime_pow:
            ans = (ans * pow(i, prime_pow[i], mod)) % mod
