def insert(s, i, N, root, temp):
    if (i == N):
        return temp
    if (s[i] == 'L'):
        root .left = insert(s, i + 1, N, root .left, temp)
    else:
        root .right = insert(s, i + 1, N, root .right, temp)
    return root


def SBTUtil(root, sum):
    if (root is None):
        return [0, sum]
    if (root .left is None and root .right is None):
        return [root .data, sum]
    left, sum = SBTUtil(root .left, sum)
    right, sum = SBTUtil(root .right, sum)
    if (root .left and root .right):
        if ((left %
             2 == 0 and right %
             2 != 0) or (left %
                         2 != 0 and right %
                         2 == 0)):
            sum += root .data
    return [left + right + root .data, sum]


