    start = 0
    end = n - 1
    while ((end - start) > 1):
        mid = (start + end) // 2
        tp = math .sqrt(p[mid])
        if (tp > (rad * 1.0)):
            end = mid - 1
        else:
            start = mid
    tp1 = math .sqrt(p[start])
    tp2 = math .sqrt(p[end])
    if (tp1 > (rad * 1.0)):
        return 0
    elif (tp2 <= (rad * 1.0)):
        return end + 1
    else:
