def pairProductMean(arr, N):
    suffixSumArray = [0] * N
    suffixSumArray[N - 1] = arr[N - 1]
    for i in range(N - 2, -1, -1):
        suffixSumArray[i] = suffixSumArray[i + 1] + arr[i]
    length = (N * (N - 1)) // 2
    res = 0
    for i in range(N - 1):
        res += arr[i] * suffixSumArray[i + 1]
    mean = 0
    if (length != 0):
        mean = res / length
    else:
        mean = 0
    return mean


