    print(round(pairProductMean(arr, N), 2))

def find_max(v, n):
    count = 0
    if (n >= 2):
        count = 2
    else:
        count = 1
    for i in range(1, n - 1):
        if (v[i - 1][0] > (v[i][0] + v[i][1])):
            count += 1
        elif (v[i + 1][0] > (v[i][0] + v[i][1])):
            count += 1
            v[i][0] = v[i][0] + v[i][1]
        else:
            continue
    return count


