def minCost(N, P, Q):
    cost = 0
    while (N > 0):
        if (N & 1):
            cost += P
            N -= 1
        else:
            temp = N // 2
            if (temp * P > Q):
                cost += Q
            else:
                cost += P * temp
            N //= 2
    return cost


