// Find the sum of elements of the Matrix generated by the given rules. 
 #include <iostream> 
 using namespace std ;
 int sum ( int A , int B , int R ) {
 
     if ( R == 0 ) {
         return A ;
     }
     else {
         return sum ( A + B, A, R - 1 ) ;
     }
 }
 int main ( ) {
     int A, B, R ;
     cin >> A >> B >> R ;
     cout << sum ( A, B, R ) ;
     return 0 ;
 }
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 
 